<script>
  export default {
    layout: 'header',
    head() {
      return {
        title: this.$config.appName
      }
    },
    async mounted () {
      let herramientasreq = await this.$axios.$get('/herramienta/')
      this.isLoading = false;
      this.herramientas = herramientasreq.data;
      let anotacionesload = await this.$axios.$get('/comentario/todos/');
      this.anotaciones = anotacionesload.data;
      console.log(this.anotaciones);
      console.log(this.herramientas);
    },
    data() {
      return {
        searchKey: '',
        tableDataKey: 0,
        checkedItems: [],
        herramientas: [],
        anotaciones: [],
        herramientasEliminadas: [],
        isEmpty: false,
        isLoading: true,
        isPaginated: true,
        paginationPosition: 'both',
        currentPage: 1,
        perPage: 10,
        isCardModalActive: false,
        paginationOrder: 'is-centered',
        idEliminar: 0,
        filtroVencer: false,
        claseSelect: 'botonfiltro',
      }
    },
    methods: {
      vencimientoPronto(fecha, renovacion){
          var d1 = new Date(fecha);
          var d2 = new Date();
          var diff = d1.getTime() - d2.getTime();
          var dias = diff / (1000 * 60 * 60 * 24);
          var diastruncados = ~~dias;
          return diastruncados <= 15 && !renovacion ? true : false;
      },
      vencida(fecha, renovacion){
          var d1 = new Date(fecha);
          var d2 = new Date();
          var diff = d1.getTime() - d2.getTime();
          var dias = diff / (1000 * 60 * 60 * 24);
          var diastruncados = ~~dias;
          return diastruncados <= 0 && !renovacion ? true : false;
      },
      // Take given plan name and return in english
      planName(plan) {
        const nombreplan = plan;
        console.log(nombreplan);
        if(nombreplan == "mensual") {
          console.log('Mensual');
          nombreplan == 'Monthly'
          return 'Monthly';
        } else if(nombreplan == "anual") {
          console.log('Anual');
          nombreplan == 'Yearly'
          return 'Yearly';
        }
      },
      borrarPopup(id){
        this.idEliminar = id;
        this.isCardModalActive = true;
      },
      cancelarPopup() {
        this.isCardModalActive = false;
      },
      async eliminarItem(){
        console.log(this.idEliminar);
        const eliminar = await this.$axios.$delete('/herramienta/' + this.idEliminar);
        this.herramientasEliminadas.push(this.idEliminar);
        console.log(this.herramientasEliminadas);
        this.isCardModalActive = false;
        console.log(this.filteredHerramientas);
        this.$buefy.toast.open({
              message: 'App deleted.',
              type: 'is-success'
        })
      },
      notificacionesPopup(){
        this.filtroVencer = !this.filtroVencer;
        if(this.filtroVencer) {
          this.claseSelect = "botonfiltro-selected";
        } else {
          this.claseSelect = "botonfiltro"
        }
      },
      fueEliminado(id){
        if(this.herramientasEliminadas.includes(id)){
          return true;
        }
        return false;
      },
      async eliminarAnotacion (id) {
        console.log("Eliminando comentario");
        const eliminar_coment = await this.$axios.$delete('/comentario/comment/' + id);      
        const anotacioneseliminadas = this.anotaciones.filter(i => i.commentid !== id);
        this.anotaciones = anotacioneseliminadas;
        this.$buefy.toast.open({
              message: 'Annotation removed',
              type: 'is-success'
          })
      },      
    },
    computed : {
      filteredHerramientas: {
           get(){
            this.herramientasFiltro1 = this.herramientas.filter(herramienta => herramienta.nombre.toLowerCase().indexOf(this.searchKey.toLowerCase()) !== -1);
            this.herramientasFiltro2 = this.herramientasFiltro1.filter(herramienta => this.fueEliminado(herramienta.id)== false);
            if(this.filtroVencer){
              const herramientasVencer = this.herramientasFiltro2.filter(herramienta => this.vencimientoPronto(herramienta.vencimiento, herramienta.renovacion) !== false);
              this.herramientasFiltro2 = herramientasVencer;
            }; 
            return this.herramientasFiltro2;
          }
      }
    }
  }
</script>

